import logo from './logo.svg';
import './App.css';
import Sidebar from './components/Sidebar';
import Movie from './components/Movie';

function App() {
  const [SidebarShow,setSideBarShow]=useState(false);
  return (
    <div className="App">
      <Sidebar/>
      <p onClick={setSideBarShow(!SidebarShow)}>Click me to show sidebar</p>
      <Movie/>
    </div>
  );
}

export default App;
